INSERT INTO `oc_product_description` VALUES(68, 2, '麵包超人數珠珠機', '&lt;p&gt;Anpanman 麵包超人數珠珠機 原裝日本進口 新品未開封 現貨&lt;/p&gt;\r\n\r\n&lt;p&gt;通過數小粒圓球，　訓練小朋友數學和集中力&lt;br /&gt;\r\n當珠珠通過宝宝轉動圓盤，螺旋杆會同時轉動，把珠珠送到樹頂，　&lt;/p&gt;\r\n\r\n&lt;p&gt;按下樹頂的推杆，珠珠會掉下，　並會一層層的掉下&lt;/p&gt;\r\n\r\n&lt;p&gt;產品尺寸:•H35.0ｘW18.5ｘD17.5cm&lt;/p&gt;\r\n\r\n&lt;p&gt;適合年齡: 2 歲以上&lt;/p&gt;\r\n', '', '', '');
