INSERT INTO `oc_product_description` VALUES(87, 2, 'Peppa Pig 男童短袖T恤 (藍色簡條/消防員) ', '&lt;p&gt;Peppa Pig小男孩T-Shirt, 顏色繽紛, 快啲為小寶寶添置夏日新裝啦!!&lt;br /&gt;\r\n全部出口貨, 純棉料, 夏天最啱吸汗~~~&lt;/p&gt;\r\n\r\n&lt;p&gt;尺寸由歲半至6歲都有, 可參考下表, 並先詢問是否有貨&lt;br /&gt;\r\n18-24m 92cm&lt;br /&gt;\r\n&amp;nbsp;2-3T 98cm&lt;br /&gt;\r\n&amp;nbsp;3-4T 104cm&lt;br /&gt;\r\n&amp;nbsp;4-5T 110cm&lt;br /&gt;\r\n&amp;nbsp;5-6T 116cm&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(72, 2, 'Carter''s 夏季純棉男嬰夾衣 (6 Months)', '&lt;p&gt;這些短袖夾衣很可愛，方便爬行的寶寶&lt;br /&gt;\r\n• 絲滑柔軟的棉, 嬰兒穿得舒服&lt;br /&gt;\r\n• 底部啪鈕式方便更換尿片&lt;br /&gt;\r\n• 彈性肩膀設計，方便過頭穿&lt;br /&gt;\r\n•100％純棉&lt;br /&gt;\r\n•可機洗&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(51, 1, 'Anpanman Fire Engine', '&lt;h2&gt;&lt;font color=&quot;indigo&quot; size=&quot;4&quot;&gt;Glow light with fireman sound&lt;br /&gt;\r\nTruck move forwards and backwards and Anpanman driver wave his hand up and down&lt;br /&gt;\r\nPress the buttons o-n fire truck top with 2 difference conversation mode. &lt;/font&gt;&lt;/h2&gt;\r\n\r\n&lt;h2&gt;&lt;font color=&quot;indigo&quot; size=&quot;4&quot;&gt;Product Dimension 20cm x 13cm x 19cm&lt;/font&gt;&lt;/h2&gt;\r\n\r\n&lt;h2&gt;&lt;font color=&quot;#4b0082&quot; size=&quot;4&quot;&gt;Age grade: 24 months and up&lt;/font&gt;&lt;/h2&gt;\r\n\r\n&lt;p&gt;&lt;a href=&quot;https://www.youtube.com/watch?feature=player_embedded&amp;amp;v=6zXrzqFZTnI&quot;&gt;&lt;u&gt;&lt;font color=&quot;#4b0082&quot; size=&quot;4&quot;&gt;Item Demonstration&lt;/font&gt;&lt;/u&gt;&lt;/a&gt;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(51, 2, '麵包超人消防車', '&lt;p&gt;勇敢的麵包超人又出動啦! 麵包超人消防車會發出火警燈和聲音, 消防車移動時, 麵包超人雙手會向上向下搖擺, 而寶寶只要按火警鍵, 麵包超人便會發出兩種不同的對話, 非常可愛!&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;h2&gt;&lt;font size=&quot;6&quot;&gt;&lt;font size=&quot;4&quot;&gt;產品尺寸：20cm x 13cm x 19cm&lt;/font&gt;&lt;/font&gt;&lt;/h2&gt;\r\n\r\n&lt;h2&gt;&lt;font size=&quot;6&quot;&gt;&lt;font face=&quot;Arial&quot; size=&quot;4&quot;&gt;適合歲數：　24 個月以上&lt;/font&gt;&lt;/font&gt;&lt;/h2&gt;\r\n\r\n&lt;p&gt;&lt;u&gt;&lt;a href=&quot;https://www.youtube.com/watch?feature=player_embedded&amp;amp;v=6zXrzqFZTnI&quot;&gt;&lt;font size=&quot;6&quot;&gt;&lt;font face=&quot;Arial&quot; size=&quot;4&quot;&gt;產品短片&lt;/font&gt;&lt;/font&gt;&lt;/a&gt;&lt;/u&gt;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(79, 2, 'Nikko Hello Kitty GO GO 搖控飛機車', '&lt;p&gt;Hello Kitty 無線搖控飛機可訓練寶寶的操控投巧, 有別於傳統只用手推的玩具。寶寶可利用專為他們的小手而設計的可愛屋仔搖控, 使Kitty飛機移前或後, 小小的螺旋掌也會同時轉動呢! Hello Kitty 的頭部亦會隨飛機滑行而左右搖擺, 非常可愛! 操作簡單容易!&lt;/p&gt;\r\n\r\n&lt;p&gt;適合年齡: 12 個月以上&lt;/p&gt;\r\n\r\n&lt;p&gt;產品尺寸: 18 x 16 x 15 cm&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(53, 1, 'vtech Playtime Bus with phonics', '&lt;p&gt;Interactive talking bus teaches phonics, letter names and numbers through 4 modes of play. Select your destination from the school, zoo or train station to hear a sing-along song or fun phrase. Chunky buttons on the top of the bus call out shapes while the smiling passengers say funny phrases when pressed. Features a wide selection of musical melodies in various styles and fun sound effects including horn, brake and accelerator!&lt;/p&gt;\r\n\r\n&lt;p&gt;Playtime Bus Features&lt;br /&gt;\r\nAcross mode quiz encourages listening skills and repetitive play.&lt;br /&gt;\r\nEncourages language development and independent play.&lt;br /&gt;\r\n4 modes of play.&lt;br /&gt;\r\nIncludes fun character buttons, destination selector, colour and shape buttons.&lt;br /&gt;\r\n26 raised letter buttons to encourage letter recognition.&lt;br /&gt;\r\nInteractive talking bus teaches phonic sounds, letter names and numbers.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(53, 2, 'vtech 字母巴士', '&lt;p&gt;&quot;字母巴士&quot; 屬於偉易達&quot;成長第一步&quot; 系列產品中的一款, 專為1-3歲以上幼兒綜合能力開發而設計的電子教育產品。字母巴士有兩種聲音模式。小朋友通過字母巴士內置的4個游戲認識字母和數字, 學習字母的發音,認識顏色,辯別形狀,學習常用單詞, 欣賞動聽的歌曲和美妙的音樂。&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(54, 1, 'vtech Soft Singing Phone', '&lt;p&gt;This toy mobile phone with soft fabric has been specially designed for role-play fun while developing baby’s senses. Non-breakable mirror promotes self-awareness, mixture of fabric and plastic invites tactile exploration while the bright colours and flashing lights promote visual awareness and exploration. Press the chunky light-up buttons to hear fun early learning phrases or spin the roller to activate a song. Includes lots of realistic phone sounds, cute phrases and animal sound effects.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;Features:&lt;/p&gt;\r\n\r\n&lt;ul id=&quot;features&quot;&gt;\r\n	&lt;li&gt;Includes animal sound effects, phrases and a fun song.&lt;/li&gt;\r\n	&lt;li&gt;Textured material and chunky press-buttons for tactile play.&lt;/li&gt;\r\n	&lt;li&gt;Includes lots of realistic phone sound effects.&lt;/li&gt;\r\n	&lt;li&gt;Features non-breakable mirror, rolling beads and flashing lights.&lt;/li&gt;\r\n	&lt;li&gt;Helps develops early language skills and reading concepts.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(54, 2, 'vtech 音樂電話', '&lt;p&gt;vtech玩具電話，設計上加添了柔軟的面料，為孩子們增加角色扮演的樂趣，同時發展孩子們的感知能力。不易碎的鏡子，助提升自我認知，不同物料的交織，助小朋友的觸感探索；另外，明亮的色彩和閃爍的燈光有助促進視覺專注力和探索。按矮胖亮燈按鈕，可聽到有趣的學習短語或旋轉滾筒，可播放歌曲。包括大量的真實電話的聲音，有趣的短語和動物的聲音效果。&lt;/p&gt;\r\n\r\n&lt;p&gt;特徵&lt;br /&gt;\r\n轉動滾筒可播放歌曲。&lt;br /&gt;\r\n包括大量的現實電話的聲音效果。&lt;br /&gt;\r\n不同物料交織的設計和有趣的按鈕助觸覺發展。&lt;br /&gt;\r\n包括不易碎的鏡子，滾動珠子和閃光。&lt;br /&gt;\r\n包括動物的聲音效果，短語和有趣的歌曲&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(98, 1, 'Baby cotton velvet Backpack (Pink Rabbit / Blue Bear)', '&lt;p&gt;Suitable for 1-4 years old baby. The&amp;nbsp;backpack is made ​​of cotton velvet, which is very soft material and&amp;nbsp;machine washable. Bunny and Bear&amp;nbsp;can become baby''s playmates.&amp;nbsp;When you&amp;nbsp;tap Bunny and Bear, there will be&amp;nbsp;BEEP BEEP sound!&amp;nbsp;Parents can train your baby to take care&amp;nbsp;the Bunny or Bear. Baby, don''t forget to&amp;nbsp;bring your playmates out to play!&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\nSize:&lt;br /&gt;\r\nBackpacks long 19CM, width 22CM, high-24CM&lt;br /&gt;\r\nDoll long 20CM, width 14CM&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
