INSERT INTO `oc_product_description` VALUES(65, 1, 'Disney Dusty Activity Ride-on', '&lt;p&gt;Your toddler can have a great ‘flying’ adventure thanks to the Dusty Activity Plane - complete with real spinning propellor.&lt;/p&gt;\r\n\r\n&lt;p&gt;This courageous crop duster is straight from the Disney Pixar Planes movie and has lots of features that kids will love.&lt;/p&gt;\r\n\r\n&lt;p&gt;Press Dusty’s buttons for realistic sounds and flashing lights including take-off and landing noises.&lt;/p&gt;\r\n\r\n&lt;p&gt;What’s more, Dusty is a big fan of rock music so press the orange button to hear his favourite tunes.&lt;/p&gt;\r\n\r\n&lt;p&gt;Comfy to ride for toddlers aged one to three years, Dusty is a dream-come-true for little Disney fans.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;Product Size: 58cm x 28cm x 50cm&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;Battery specifications: 3 x AA&lt;/p&gt;\r\n\r\n&lt;p&gt;Product complied to ASTM (US) and CE (European Nations) testing requirement&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
