INSERT INTO `oc_product_description` VALUES(164, 1, 'Fisher Price - Baby Animals Counting Soft Book', '&lt;p&gt;Baby animals are so cute - and so much fun to count! Children will delight to find their favorite babies running wild, flying high, and swimming all over the Precious Planet. Fun-to-feel textures, peek-a-boo flaps, a love-to-shake rattle and many thrilling crinkle sounds invite babies and tots to explore, helping them develop important motor skills.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(164, 2, 'Fisher Price - 數數小動物布書', '&lt;p&gt;第一頁：One baby lion lets out a big roar&lt;/p&gt;\r\n\r\n&lt;p&gt;While two baby elephants splash more and more.&lt;/p&gt;\r\n\r\n&lt;p&gt;第二頁： Three baby giraffes munch a green leafy lunch !&lt;/p&gt;\r\n\r\n&lt;p&gt;Four baby rhinos race around in a bunch.&lt;/p&gt;\r\n\r\n&lt;p&gt;第三頁：Five baby flamingoes will swim and eat, while a little crocodile lies at their feet.&lt;/p&gt;\r\n\r\n&lt;p&gt;第四頁：Six baby pelicans fly high in the sky.&lt;/p&gt;\r\n\r\n&lt;p&gt;Hippo plays in the water,while zebra stays dry.&lt;/p&gt;\r\n\r\n&lt;p&gt;第五頁：Seven baby polar bears slip and slide on the ice.&lt;/p&gt;\r\n\r\n&lt;p&gt;第六頁 ：Eight baby penguins think the water feels nice.&lt;/p&gt;\r\n\r\n&lt;p&gt;第七頁： Nine little fish flash colors so bright.&lt;/p&gt;\r\n\r\n&lt;p&gt;第八頁：Ten baby starfish stay out of sight.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;每一頁都附帶搖鈴，響紙等等&lt;/p&gt;\r\n\r\n&lt;p&gt;如: 第一頁小獅子的尾巴是毛毛茸茸的，可以讓寶寶拉扯，第二頁的樹葉是響紙，捏起來沙沙響....&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(129, 1, 'vtech Little Singing Alfie', '&lt;p&gt;Hug your way to adorable early learning! Press the 3 light-up buttons on Little Singing Alfie’s tummy to hear fun phrases and sound effects that introduce emotions, numbers and counting with popular tunes. Sing-along song and an English speaking voice promotes recognition and accelerates learning. Pressing the buttons develops fine motor skills while senses are stimulated by the flashing lights, fun sounds and a mixture of plush and plastic. Attachment strap makes Alfie easily portable!&lt;/p&gt;\r\n\r\n&lt;p&gt;Age grade: 3 months and up&lt;/p&gt;\r\n\r\n&lt;p&gt;Product Features:&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;Introduces emotions, numbers and counting.&lt;br /&gt;\r\n3 chunky, coloured light-up buttons.&lt;br /&gt;\r\nVelcro-like attachment strap for attaching to cot or pushchair.&lt;br /&gt;\r\nIncludes lots of fun, friendly phrases and sound effects.&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;Product Size:&amp;nbsp;17cm x 11cm x 18.5cm&lt;/p&gt;\r\n\r\n&lt;p&gt;Battery standard: 2 x AA&lt;/p&gt;\r\n\r\n&lt;p&gt;Product complied to ASTM (US) and CE (European Nation) testing requirement&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
