INSERT INTO `oc_product_description` VALUES(167, 2, 'siku 城市巴士1:87', '&lt;p&gt;城市單層巴士的壓鑄微型金屬玩具模型。平穩運行的輪軸，設計和有吸引力的外觀。 德國設計SIKU玩具符合最高的質量和安全要求&lt;/p&gt;\r\n\r\n&lt;p&gt;風靡歐美，造型多樣的小汽車&lt;br /&gt;\r\n各種擬真的車子直接縮小成可愛的小車&lt;br /&gt;\r\n擁有金屬質感的高品質&lt;/p&gt;\r\n\r\n&lt;p&gt;產品尺寸: 13 x 3.5 x 4 cm&lt;/p&gt;\r\n\r\n&lt;p&gt;金屬製造 德國品牌精緻堅實&lt;br /&gt;\r\n吊卡包裝&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
