INSERT INTO `oc_product_description` VALUES(78, 1, 'vtech Counting Fun Elephant', '&lt;p&gt;VTech''s Counting Fun Elephant combines learning with fun as your child distributes colorful balls through an air popper. It''s up to a little birdie perched in a nearby nest to decide which path the balls can take, either through the fountain or over the piano keys. While the toy teaches numbers and counting, the elephant''s cap lights up and the piano plays music that keeps your child engaged for endless playtime.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;Features:&lt;/p&gt;\r\n\r\n&lt;p&gt;Comes with three colorful balls&lt;br /&gt;\r\nFour piano keys to teach numbers and counting&lt;br /&gt;\r\nAdds learning to air-popping fun&lt;br /&gt;\r\nTwo modes of play&lt;/p&gt;\r\n\r\n&lt;p&gt;Age grade: 9 months and up&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;a href=&quot;https://www.youtube.com/watch?feature=player_embedded&amp;amp;v=da4danNnIEE&quot;&gt;Product Youtube Video&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
