INSERT INTO `oc_product_description` VALUES(248, 1, 'Baby Kitty - Baby Pop-up Straw Cup', '&lt;p&gt;- For 8m up babies learn how to use a straw to drink&lt;br /&gt;\r\n- Special bounce design allows easier use&lt;br /&gt;\r\n- Intimate grip design, easy to train your baby to learn to drink on their own&lt;br /&gt;\r\n- Safety bounce cup lid design&lt;br /&gt;\r\n- Easy for the mother to carry on travel&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
