INSERT INTO `oc_product_description` VALUES(155, 1, 'vtech Hello Kitty softbook', '&lt;p&gt;This soft tactile, fabric book is perfect for exploring with baby! Each page features a different scene with Hello Kitty and her friends, Joey, Tiny Chum, Tippy, Cathy and Rory and a fun press button introducing a number, shape, colour and object. Includes 5 melodies and a fun sing-along song. Light-up happy sun attracts babys eye and encourages interaction.&lt;/p&gt;\r\n\r\n&lt;p&gt;Age grade: 3 months and up&lt;/p&gt;\r\n\r\n&lt;p&gt;Product Size: H19.3, W25.6, D5.9cm.&lt;/p&gt;\r\n\r\n&lt;p&gt;https://www.facebook.com/inspiretoystore/photos/pcb.1434336290156230/1434333860156473/?type=1&amp;amp;theater&lt;/p&gt;\r\n\r\n&lt;p&gt;Features:&lt;/p&gt;\r\n\r\n&lt;p&gt;•Aids the development of motor skills and basic language.&lt;br /&gt;\r\n•Hello Kitty appears in different scenes and offers fun voice responses.&lt;br /&gt;\r\n•Light-up sun to attract babys eye.&lt;br /&gt;\r\n•Attaches easily to cot or pushchair.&lt;br /&gt;\r\n•Batteries required: 2 x AAA (included).&lt;br /&gt;\r\n•For ages 3 months and over.&lt;/p&gt;\r\n\r\n&lt;p&gt;Product compled to the ASTM (United States) and EN (European National) testing requirements&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(111, 1, 'vtech Hello Kitty Melody Mobile', '&lt;p&gt;Hello Kitty Melody Mobile&lt;/p&gt;\r\n\r\n&lt;p&gt;This magical mobile, with gently spinning leaves, soft fabric Hello Kitty characters and soft glowing night-light, helps soothe baby off to sleep. Featuring 3 different modes of play with 20 melodies, 20 lullabies, 4 sung-songs and soothing nature sounds. There is also a timer that can be set to 10, 15 or 20 minutes to suit baby’s sleep patterns. A sound sensor will detect if baby wakes and trigger the gentle melodies to help soothe baby.&lt;/p&gt;\r\n\r\n&lt;p&gt;Features:&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; Gentle spinning motion to soothe baby&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; 3 modes with 10, 15 and 20 minute timer settings&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; Beautiful mobile in lovely Hello Kitty design&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; Features soft glowing night light&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; Includes 40 soothing melodies and 4 sung songs&lt;/p&gt;\r\n\r\n&lt;p&gt;Product Size: 40 x46 x 22cm&lt;/p&gt;\r\n\r\n&lt;p&gt;Age grade: birth and up&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
