INSERT INTO `oc_product_description` VALUES(277, 1, 'Usborne - My First Word Book', '&lt;p&gt;A word book that intends to help young children build their vocabulary.&lt;/p&gt;\r\n\r\n&lt;p&gt;It comes with over 270 words in this delightfully illustrated book. Themes include numbers, animals, on the farm, the body, things that go and bedtime.&lt;/p&gt;\r\n\r\n&lt;ul style=&quot;list-style-type: none; margin: 0px; padding-right: 0px; padding-left: 0px; color: rgb(0, 0, 0); font-family: verdana, arial, helvetica, sans-serif; font-size: small; line-height: normal;&quot;&gt;\r\n	&lt;li style=&quot;margin: 0.5em 0em;&quot;&gt;&amp;nbsp;&lt;/li&gt;\r\n	&lt;li style=&quot;margin: 0.5em 0em;&quot;&gt;&lt;b&gt;Board book:&lt;/b&gt;&amp;nbsp;20 pages&lt;/li&gt;\r\n	&lt;li style=&quot;margin: 0.5em 0em;&quot;&gt;&lt;b&gt;Publisher:&lt;/b&gt;&amp;nbsp;Usborne Publishing Ltd (1 Sep 2013)&lt;/li&gt;\r\n	&lt;li style=&quot;margin: 0.5em 0em;&quot;&gt;&lt;b&gt;Language:&lt;/b&gt;&amp;nbsp;English&lt;/li&gt;\r\n	&lt;li style=&quot;margin: 0.5em 0em;&quot;&gt;&lt;b&gt;ISBN-10:&lt;/b&gt;&amp;nbsp;1409551830&lt;/li&gt;\r\n	&lt;li style=&quot;margin: 0.5em 0em;&quot;&gt;&lt;b&gt;ISBN-13:&lt;/b&gt;&amp;nbsp;978-1409551836&lt;/li&gt;\r\n	&lt;li style=&quot;margin: 0.5em 0em;&quot;&gt;&lt;b&gt;Product Dimensions:&amp;nbsp;&lt;/b&gt;25.2 x 21.8 x 1.4 cm&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(277, 2, 'Usborne - My First Word Book', '&lt;p&gt;此書旨在幫助幼兒建立自己的詞彙&lt;br /&gt;\r\n它配備了超過270的&lt;span style=&quot;font-size: 13px;&quot;&gt;詞彙, 及生動的&lt;/span&gt;圖鑑&lt;br /&gt;\r\n主題包括數字、動物、農場、身體、周邊的事物和睡前&lt;span style=&quot;font-size: 13px;&quot;&gt;詞彙&lt;/span&gt;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(278, 1, 'Usborne - Baby''s very first touchy-feely colours play book', '&lt;p style=&quot;margin: 1.12em 0px; padding: 0px; color: rgb(0, 0, 0); font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;&quot;&gt;Baby''s very first touchy-feely colours play book&lt;/p&gt;\r\n\r\n&lt;ul style=&quot;margin: 1em 1em 1em 0px; padding-right: 0px; list-style: none; color: rgb(0, 0, 0); font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;&quot;&gt;\r\n	&lt;li style=&quot;margin: 0px; padding: 0px; list-style: disc outside;&quot;&gt;Babies will love looking at the bright pictures and feeling the textures as they explore the vibrant colours in this fun board book.&lt;/li&gt;\r\n	&lt;li style=&quot;margin: 0px; padding: 0px; list-style: disc outside;&quot;&gt;Each page is specially designed to be visually stimulating to babies, with bright, eye-catching illustrations and lots to discover.&lt;/li&gt;\r\n	&lt;li style=&quot;margin: 0px; padding: 0px; list-style: disc outside;&quot;&gt;Features a page for each colour, from red strawberries and tomatoes growing in a vegetable patch, to blue dolphins playing in the sea and yellow flowers in a sunny field&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color: rgb(0, 0, 0); font-family: Helvetica, Arial, sans-serif; line-height: 16px;&quot;&gt;Baby''s Very First Books series was crowned winner of the Best Preschool Publishing Range category in the first ever Progressive Preschool Awards. A judging panel comprised of buyers from top names such as Harrods, Mothercare, Boots, Toys R Us, Selfridges, Sainsbury’s, JoJoMamanBebe and Waitrose selected the winners in each of the award''s four categories.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;margin: 0px 0px 15px; padding: 0px; color: rgb(0, 0, 0); font-family: Helvetica, Arial, sans-serif; line-height: 13px; background-color: rgb(242, 242, 242);&quot;&gt;&lt;strong&gt;Age:&lt;/strong&gt;&amp;nbsp;Birth+&lt;/p&gt;\r\n\r\n&lt;p style=&quot;margin: 0px 0px 15px; padding: 0px; color: rgb(0, 0, 0); font-family: Helvetica, Arial, sans-serif; line-height: 13px; background-color: rgb(242, 242, 242);&quot;&gt;&lt;strong&gt;Board book:&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;margin: 0px 0px 15px; padding: 0px; color: rgb(0, 0, 0); font-family: Helvetica, Arial, sans-serif; line-height: 13px; background-color: rgb(242, 242, 242);&quot;&gt;ISBN: 9781409565116&lt;br /&gt;\r\n10 pages&lt;br /&gt;\r\n185 x 185mm&lt;/p&gt;\r\n\r\n&lt;p style=&quot;margin: 0px 0px 15px; padding: 0px; color: rgb(0, 0, 0); font-family: Helvetica, Arial, sans-serif; line-height: 13px; background-color: rgb(242, 242, 242);&quot;&gt;&lt;strong&gt;Author/Editor:&lt;/strong&gt;&amp;nbsp;Fiona Watt&lt;/p&gt;\r\n\r\n&lt;p style=&quot;margin: 0px 0px 15px; padding: 0px; color: rgb(0, 0, 0); font-family: Helvetica, Arial, sans-serif; line-height: 13px; background-color: rgb(242, 242, 242);&quot;&gt;&lt;strong&gt;Illustrator:&lt;/strong&gt;&amp;nbsp;Stella Baggott&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(278, 2, 'Usborne - Baby''s very first touchy-feely colours play book', '&lt;p&gt;&lt;span style=&quot;line-height: 1.6em;&quot;&gt;這書有寶寶喜歡看的&lt;/span&gt;&lt;span style=&quot;font-size: 13px;&quot;&gt;色彩&lt;/span&gt;&lt;span style=&quot;line-height: 1.6em;&quot;&gt;圖片和不同的紋理，可讓他們探索這本有趣的紙板書。&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;每一頁&lt;span style=&quot;font-size: 13px;&quot;&gt;的插圖&lt;/span&gt;是專門設計為&lt;span style=&quot;font-size: 13px;&quot;&gt;刺激嬰兒的&lt;/span&gt;視覺。&amp;nbsp;&lt;br /&gt;\r\n設有各種豐富的顏色，從紅色的草莓和蕃茄的菜地，&lt;span style=&quot;font-size: 13px;&quot;&gt;海中的&lt;/span&gt;藍海豚和黃色的花朵在陽光中現場演奏等..&lt;/p&gt;\r\n\r\n&lt;p&gt;Baby''s very first系列叢書獲得最佳學前出版獎項。&lt;span style=&quot;font-size: 13px;&quot;&gt;評判團選&lt;/span&gt;從頂級品牌如&lt;span style=&quot;color: rgb(0, 0, 0); font-family: Helvetica, Arial, sans-serif; font-size: 13px; line-height: 16px;&quot;&gt;Harrods, Mothercare, Boots, Toys R Us, Selfridges, Sainsbury’s, JoJoMamanBebe, Waitrose等&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;font-size: 13px; margin: 0px 0px 15px; padding: 0px; color: rgb(0, 0, 0); font-family: Helvetica, Arial, sans-serif; line-height: 13px; background-color: rgb(242, 242, 242);&quot;&gt;&lt;strong&gt;適合年齡&lt;/strong&gt;&lt;strong&gt;:&lt;/strong&gt;&amp;nbsp;NB+&lt;/p&gt;\r\n\r\n&lt;p style=&quot;font-size: 13px; margin: 0px 0px 15px; padding: 0px; color: rgb(0, 0, 0); font-family: Helvetica, Arial, sans-serif; line-height: 13px; background-color: rgb(242, 242, 242);&quot;&gt;&lt;strong&gt;硬皮書:&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;font-size: 13px; margin: 0px 0px 15px; padding: 0px; color: rgb(0, 0, 0); font-family: Helvetica, Arial, sans-serif; line-height: 13px; background-color: rgb(242, 242, 242);&quot;&gt;ISBN: 9781409565116&lt;br /&gt;\r\n10頁&lt;br /&gt;\r\n185 x 185mm&lt;/p&gt;\r\n\r\n&lt;p style=&quot;font-size: 13px; margin: 0px 0px 15px; padding: 0px; color: rgb(0, 0, 0); font-f