INSERT INTO `oc_product_description` VALUES(272, 1, 'Merries, Air Through, NB96 (Plus Version) (Newborn - 5kg)', '&lt;p&gt;&lt;span style=&quot;color: rgb(0, 0, 0); line-height: 16px; font-family: Verdana, sans-serif; font-size: small;&quot;&gt;Its unique skin-friendly wavy top sheet allows air to flow freely between the diaper and the skin so it does not stick to the baby''s skin! With its superb absorbency, exceptional breathability and ultra softness, Merries Tape diapers provide the dryness and comfort that every baby’s delicate skin needs!&lt;/span&gt;&lt;/p&gt;\r\n', '', '', '');
