INSERT INTO `oc_product_description` VALUES(305, 1, 'siku Yellow Double Decker Bus', '&lt;div style=&quot;font-family: Verdana, Helvetica, Arial, sans-serif; font-size: 13px; line-height: normal; color: rgb(0, 0, 205);&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;A Siku branded, high quality die-cast vehicle.&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;font-family: Verdana, Helvetica, Arial, sans-serif; font-size: 13px; line-height: normal; color: rgb(0, 0, 205);&quot;&gt;&amp;nbsp;\r\n&lt;div&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;This model is built to 1:87 scale.&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/div&gt;\r\n\r\n&lt;div&gt;&amp;nbsp;&lt;/div&gt;\r\n\r\n&lt;div&gt;&amp;nbsp;&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div style=&quot;font-family: Verdana, Helvetica, Arial, sans-serif; font-size: 13px; line-height: normal; color: rgb(0, 0, 205);&quot;&gt;&amp;nbsp;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;font-family: Verdana, Helvetica, Arial, sans-serif; font-size: 13px; line-height: normal; color: rgb(0, 0, 205);&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;&lt;span style=&quot;font-size: 12pt;&quot;&gt;Dimensions (Length x Width x Height):&amp;nbsp; 16.0cm x 3.2cm x 4.9cm&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\nThe MAN Doubledecker buses that are famous in Berlin are not just an eye-catching model in the original.&amp;nbsp;&lt;br /&gt;\r\n&lt;br /&gt;\r\nEven the 1:87 metal model by SIKU makes it possible to guess the true size of this bus.&amp;nbsp;&lt;br /&gt;\r\n&lt;br /&gt;\r\nThe 3-axle model runs on easy-tread tyres out of rubber material.&amp;nbsp;&lt;br /&gt;\r\n&lt;br /&gt;\r\nThe extremely detailed model format in the interior and exterior is unmatchable on the toy market.&amp;nbsp;&lt;br /&gt;\r\n&lt;br /&gt;\r\nThe vehicle is both robust enough for the kids'' playroom and also a high-quality addition for the collector''s display cabinet.&amp;nbsp;&lt;/span&gt;&lt;/div&gt;\r\n', '', '', '');
