INSERT INTO `oc_product_description` VALUES(349, 1, 'Breast-feeding / Nursing Cover (Style: Big blue dots)', '&lt;p&gt;- Made of light weight and breathable 100% cotton&lt;br /&gt;\r\n- Adjustable neck strap&lt;br /&gt;\r\n- Pocket for burp clothes and nursing pads&lt;br /&gt;\r\n- Rigid neckline allows&amp;nbsp;for ventilation and you to view the baby while feeding&lt;br /&gt;\r\n- Tight feature to tighten and secure nursing cover to ensure mom is covered at the sides and back&lt;br /&gt;\r\n&lt;span style=&quot;line-height: 1.6em;&quot;&gt;- Convenient to use at home or outdoor as cover&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;line-height: 1.6em;&quot;&gt;- Easy to wash and clean&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;line-height: 1.6em;&quot;&gt;- Necessities for breast feeding mom&lt;/span&gt;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(349, 2, '出口歐美 時尚哺乳巾 (藍色大波點)', '&lt;p&gt;&lt;span style=&quot;font-size: 13px;&quot;&gt;100%純棉,&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size: 13px;&quot;&gt;非常柔軟透氣, 媽媽餵奶唔怕焗親BB~&lt;/span&gt;&lt;br style=&quot;font-size: 13px;&quot; /&gt;\r\n&lt;span style=&quot;font-size: 13px;&quot;&gt;可摺起&lt;/span&gt;&lt;span style=&quot;font-size: 13px;&quot;&gt;攜帶方便,&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size: 13px;&quot;&gt;在家或外出都適用, 清洗容易, 餵母乳媽媽必備!!!!&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n- 可調節肩帶&amp;nbsp;&lt;br /&gt;\r\n- 附有小口袋放乳墊&amp;nbsp;&lt;br /&gt;\r\n- 寬領口設計, 更通風及哺乳時可看著寶寶&amp;nbsp;&lt;br /&gt;\r\n- 哺乳巾可覆蓋媽媽的側面和背面&amp;nbsp;&lt;br /&gt;\r\n- 方便在家裡或戶外&lt;span style=&quot;font-size: 13px;&quot;&gt;使用&lt;/span&gt;&amp;nbsp;&lt;br /&gt;\r\n- 易於清洗和清潔&amp;nbsp;&lt;br /&gt;\r\n- 是母乳媽媽的&lt;span style=&quot;font-size: 13px;&quot;&gt;必需品&lt;/span&gt;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(347, 1, 'SKIP HOP - Treetop Friends Night and Day Discovery Toy', '&lt;p&gt;&lt;b style=&quot;line-height: 1.6em&quot;&gt;Baby will delight in discovery as she sees herself!&lt;/b&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Perfect for tummy time or anytime! An adorable wide-eyed owl and sweet birdie keep baby company as she learns self-recognition and engages in tactile play. Mirror stands upright or folds flat, and loops on the back allow it to attach anywhere.&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;Field of “grass ribbons” for soothing tactile play&lt;/li&gt;\r\n	&lt;li&gt;Tuggable crinkle leaves&lt;/li&gt;\r\n	&lt;li&gt;Pull and place owl rattle and birdie squeaker&lt;/li&gt;\r\n	&lt;li&gt;Stand upright with wedge or attach anywhere with loops on the back&lt;/li&gt;\r\n	&lt;li&gt;Playful patterns and linen details&lt;/li&gt;\r\n	&lt;li&gt;Perfect for on-the-go&lt;/li&gt;\r\n	&lt;li&gt;Safety-tested to meet or exceed ASTM, CPSIA, EN71 and applicable safety standards&lt;/li&gt;\r\n	&lt;li&gt;Ages 0+&lt;/li&gt;\r\n	&lt;li&gt;Size (inches): 9w x 9.5h x 5.25d&lt;/li&gt;\r\n	&lt;li&gt;Only for domestic use&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(347, 2, 'SKIP HOP - Tree Top Friends 嬰兒日與夜探索掛飾', '&lt;p&gt;小寶寶由出生開始, 就要學習日與夜, 否則日夜癲倒, 成日攪到爸爸媽媽無覺好訓呢~ 今次Skip Hop的小小貓頭鷹會教小寶寶探索日與夜, 呢款玩具可掛在床邊或嬰兒車, 附帶BB牙膠和安全小鏡~ 媽媽可在白天教寶寶轉去藍天白雲那邊, 晚上時教寶寶轉去另一邊, 讓寶寶看看小小貓頭鷹已經是時候睡覺喇~~&lt;/p&gt;\r\n\r\n&lt;p&gt;通過 ASTM, CPSIA, EN71安全檢測&lt;br /&gt;\r\n適合年齡: 3歲或以上&lt;br /&gt;\r\n尺寸: 5.5&quot;闊w x 10.5&quot;高 x 1.25&quot;厚&lt;/p&gt;\r\n', '', '', '');
