INSERT INTO `oc_product_description` VALUES(351, 2, '麵包超人嬰兒隔尿墊 (黃色 / 藍色 )', '&lt;p&gt;初生BB必備!!! 嬰兒隔尿墊, 尿墊表面是薄綿絨，下層是防水膜，質料輕薄，非常透氣防水，很適合夏天使用呢~&lt;/p&gt;\r\n\r\n&lt;p&gt;尺寸：70cm x 50cm&lt;/p&gt;\r\n\r\n&lt;p&gt;用法：&lt;br /&gt;\r\n可直接鋪在床上，防止寶寶的尿床及吐奶等污漬滲透弄髒床單、床墊, 或可摺起墊在BB車&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(352, 2, 'The Usborne Book of Art Skills (小本硬皮)', '&lt;p&gt;現在返學越來越著重創意, 周不時又要親子活動一起做勞作, 又要定期參加比賽....家長少一點創作靈感都交不到功課~&lt;br /&gt;\r\n今次天書到喇!!!&lt;br /&gt;\r\n此書介紹超過120款創意繪畫、繪圖、印刷和使用許多不同的技能和技巧做拼貼畫...&amp;nbsp;&lt;br /&gt;\r\n包括水彩、油漆飛濺效果、簡單的版畫、海綿畫、油畫棒技術、線條圖和混合媒體拼貼等&amp;nbsp;&lt;br /&gt;\r\n家長只須掌握單一的技術，就可以發揮不同的創造性呢!!!!&lt;/p&gt;\r\n\r\n&lt;p&gt;尺寸: 17x13cm&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(352, 1, 'The Usborne Book of Art Skills (Mini hardback)', '&lt;ul property=&quot;v:description&quot; style=&quot;margin: 0px 0px 12px; padding-right: 0px; padding-left: 0px; list-style-type: none; list-style-position: outside; color: rgb(0, 0, 0); font-family: Helvetica, Arial, sans-serif; font-size: 13px; line-height: 16px;&quot;&gt;\r\n	&lt;li style=&quot;margin: 0px 0px 8px; padding: 0px 0px 0px 20px; background-image: url(http://www.usborne.com/images/common/themes/standard/list-bullet.gif); background-position: 0px 3px; background-repeat: no-repeat;&quot;&gt;Inspirational guide to over 120 ideas for painting, drawing, printing and doing collage using many different skills and techniques.&lt;/li&gt;\r\n	&lt;li style=&quot;margin: 0px 0px 8px; padding: 0px 0px 0px 20px; background-image: url(http://www.usborne.com/images/common/themes/standard/list-bullet.gif); background-position: 0px 3px; background-repeat: no-repeat;&quot;&gt;Includes watercolour washes, paint splattering effects, simple print-making, sponge painting, oil pastel techniques, continuous line drawings and mixed media collage.&lt;/li&gt;\r\n	&lt;li style=&quot;margin: 0px 0px 8px; padding: 0px 0px 0px 20px; background-image: url(http://www.usborne.com/images/common/themes/standard/list-bullet.gif); background-position: 0px 3px; background-repeat: no-repeat;&quot;&gt;Arranged in project-based chapters that enable budding artists to master a single technique and use it creatively.&lt;/li&gt;\r\n	&lt;li style=&quot;margin: 0px 0px 8px; padding: 0px 0px 0px 20px; background-image: url(http://www.usborne.com/images/common/themes/standard/list-bullet.gif); background-position: 0px 3px; background-repeat: no-repeat;&quot;&gt;Size: 17x13cm&lt;/li&gt;\r\n&lt;/ul&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(353, 2, '麵包超人嬰兒零食盒', '&lt;p&gt;此嬰兒零食盒最適合放寶寶的星星餅, 中間分隔層是柔軟螺旋狀塑料片,&amp;nbsp;可訓練寶寶利用手指取出食物, 又不怕打翻, 相信媽媽最怕寶寶將食物撒到周圍都是吧!!!&lt;br /&gt;\r\n兩邊有把手, 方便寶寶抓握。麵包超人造型可愛, 可吸引寶寶對食物的興趣呢~&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;耐熱度：120度 (注意:中間軟膠隔層和外蓋不耐高溫,耐熱度是60度,***請勿微波高溫消毒, 請勿放在鍋內大火煮, 請勿放在烤箱內消毒) 只可放在消毒櫃消毒&lt;br /&gt;\r\n耐 冷 度：-30度&lt;br /&gt;\r\n適用年齡：6個月起&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size: 13px;&quot;&gt;商品尺寸：13x6CM&lt;/span&gt;&lt;br /&gt;\r\n商品重量&lt;span style=&quot;font-size: 13px;&quot;&gt;：&lt;/span&gt;110克&lt;/p&gt;\r\n\r\n&lt;p&gt;Anpanman 日本原裝&lt;br /&gt;\r\n中國制&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(353, 1, 'Anpanman Baby Snack Box', '&lt;p&gt;This baby snack box can train your baby&amp;nbsp;to use his/her&amp;nbsp;fingers to take the baby food out from the snak box. The middle compartment can avoid the baby food to spread all over the floor.&amp;nbsp;With handles on sides which enable babies&amp;nbsp;to hold the cup by themselves.&amp;nbsp;Cute Anpanman design which attracts babies&amp;nbsp;to love eating their food so much!!!&lt;/p&gt;\r\n\r\n&lt;p&gt;Heat resistance: 120 degrees (Important: The heat resistance of&amp;nbsp;middle compartment and soft outer cover&amp;nbsp;is only 60 degrees&amp;nbsp;*** Do not microwave pasteurized, do not put into the boiling pot and&amp;nbsp;do not put in the oven)&lt;br /&gt;\r\nCold resistane: -30 degrees&amp;nbsp;&lt;br /&gt;\r\nAge: 6 months up&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;Product Size: 13x6 cm&lt;br /&gt;\r\nProduct Weight: 110 grams&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
