INSERT INTO `oc_product_description` VALUES(261, 1, 'Baby Kitty - Baby milk power storage container', '&lt;p&gt;- Independent milk grid&lt;br /&gt;\r\n- Easy to carry milk powder&amp;nbsp;when&amp;nbsp;going out&lt;br /&gt;\r\n-&amp;nbsp;Convenient to&amp;nbsp;pre-set appropriate bedtime component&lt;br /&gt;\r\n- Make it easier at night when the baby needs milk&lt;br /&gt;\r\n- Three grid boxes each with a separate exit&lt;br /&gt;\r\n- Different from traditional container design to reuse the same opening&lt;br /&gt;\r\n- Heat resistance: -20 degrees to 80 degrees&lt;/p&gt;\r\n', '', '', '');
