INSERT INTO `oc_product_description` VALUES(357, 2, 'Peppa Pig - My First Storybook: Daddy Pig''s Fun Run', '&lt;p&gt;Daddy Pig''s Fun Run是基於屢獲殊榮的電視角色Peppa一個愉快的故事。&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;line-height: 1.6em;&quot;&gt;究竟Peppa爸爸會完成賽跑, 衝過終點線來籌集足夠的資金來解決學校的屋頂維修嗎？&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size: 13px;&quot;&gt;小朋友將會很喜歡這個有趣的故事和精彩的世界。&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;line-height: 1.6em;&quot;&gt;這個內容簡單的兒童故事鼓勵小朋友對早期閱讀的興趣。&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;硬皮圖書: 16 頁&lt;br /&gt;\r\n出版商: Ladybird&lt;br /&gt;\r\n圖書尺寸: 17.4 x 17 x 1.6 cm / 每本&lt;/p&gt;\r\n\r\n&lt;p&gt;My First Storybook一套共8本&lt;/p&gt;\r\n\r\n&lt;p&gt;1 The Fire Engine&lt;br /&gt;\r\n2 Daddy Pigs Fun Run&lt;br /&gt;\r\n3 Peppa Goes to the Library&lt;br /&gt;\r\n4 Peppas Washing Day&lt;br /&gt;\r\n5 Peppas First Pet&lt;br /&gt;\r\n6 Peppa and the Big Train&lt;br /&gt;\r\n7 Peppa Goes to Hospital&lt;br /&gt;\r\n8 Baby Alexander&lt;/p&gt;\r\n', '', '', '');
