INSERT INTO `oc_product_description` VALUES(362, 2, 'Peppa Pig - My First Storybook: Peppa Goes to Hospital', '&lt;p&gt;Peppa Goes to Hospital是基於屢獲殊榮的電視連續劇Peppa Pig的故事。&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;小馬&lt;span style=&quot;font-size: 13px;&quot;&gt;Pedro Pony&lt;/span&gt;很不舒服，所以Peppa和她的朋友一起去&lt;span style=&quot;font-size: 13px;&quot;&gt;Madame Gazelle&lt;/span&gt;醫院去探望他。&lt;span style=&quot;font-size: 13px;&quot;&gt;Pedro向&lt;/span&gt;大家介紹醫院的事物, 例如:&amp;nbsp;護士、醫生、食物等等。 Peppa和她的朋友發現，醫院&lt;span style=&quot;font-size: 13px;&quot;&gt;畢竟&lt;/span&gt;也不是那麼可怕呢！&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;line-height: 1.6em;&quot;&gt;這個內容簡單的兒童故事鼓勵小朋友對早期閱讀的興趣。&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;硬皮圖書: 16 頁&lt;br /&gt;\r\n出版商: Ladybird&lt;br /&gt;\r\n圖書尺寸: 17.4 x 17 x 1.6 cm / 每本&lt;/p&gt;\r\n\r\n&lt;p&gt;My First Storybook一套共8本&lt;/p&gt;\r\n\r\n&lt;p&gt;1 The Fire Engine&lt;br /&gt;\r\n2 Daddy Pigs Fun Run&lt;br /&gt;\r\n3 Peppa Goes to the Library&lt;br /&gt;\r\n4 Peppas Washing Day&lt;br /&gt;\r\n5 Peppas First Pet&lt;br /&gt;\r\n6 Peppa and the Big Train&lt;br /&gt;\r\n7 Peppa Goes to Hospital&lt;br /&gt;\r\n8 Baby Alexander&lt;/p&gt;\r\n', '', '', '');
