INSERT INTO `oc_product_description` VALUES(120, 1, 'SKIP HOP - Alphabet Zoo (Unfolding Activity Book)', '&lt;p&gt;&lt;span style=&quot;font-family: Helvetica, Arial, sans-serif; font-size: 13px; line-height: normal;&quot;&gt;Baby''s first book unfolds to become a fun-filled play center with five friendly animals all in a row. Each animal has its own initial, and loads of tactile activities will keep baby entertained and engaged.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;ul style=&quot;padding-right: 0px; padding-left: 0px; margin: 0px 0px 9px 25px; list-style-position: initial; list-style-image: initial; font-family: Helvetica, Arial, sans-serif; font-size: 13px; line-height: normal;&quot;&gt;\r\n	&lt;li style=&quot;font-size: 1em;&quot;&gt;Plush elephant with crinkle ear&lt;/li&gt;\r\n	&lt;li style=&quot;font-size: 1em;&quot;&gt;Fox with flap feet and lion with touch-and-feel mane&lt;/li&gt;\r\n	&lt;li style=&quot;font-size: 1em;&quot;&gt;Banana teether attached to monkey&lt;/li&gt;\r\n	&lt;li style=&quot;font-size: 1em;&quot;&gt;Stripey zebra includes baby-safe mirror&lt;/li&gt;\r\n	&lt;li style=&quot;font-size: 1em;&quot;&gt;Embroidered details and playful patterns&lt;/li&gt;\r\n	&lt;li style=&quot;font-size: 1em;&quot;&gt;Safety-tested to meet or exceed ASTM, CPSIA, EN71 and applicable safety standards&lt;/li&gt;\r\n	&lt;li style=&quot;font-size: 1em;&quot;&gt;Ages: 0+&lt;/li&gt;\r\n	&lt;li style=&quot;font-size: 1em;&quot;&gt;Size open (inches): 29w x 7h&lt;/li&gt;\r\n	&lt;li style=&quot;font-size: 1em;&quot;&gt;Size closed (inches): 7w x 7h&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '');
