INSERT INTO `oc_product_description` VALUES(442, 1, 'Hello Kitty Hanger', '&lt;p&gt;Don''t get hung up with too much mess. This Hello Kitty hanger is sure to help keep your home free from clutter.&lt;/p&gt;\r\n\r\n&lt;p&gt;Features:&lt;/p&gt;\r\n\r\n&lt;p&gt;●Sanrio Licensed Item&lt;/p&gt;\r\n\r\n&lt;p&gt;●Dimensions: 8.5 x 9.5 cm / 3.3&quot; x 3.7&quot;&lt;/p&gt;\r\n\r\n&lt;p&gt;●Bear the weight 3 kg&lt;/p&gt;\r\n\r\n&lt;p&gt;●Material: PET, SEBS, PS&lt;/p&gt;\r\n\r\n&lt;p&gt;●Sticker at the back for putting on the wall&lt;/p&gt;\r\n\r\n&lt;p&gt;●Transparent hanger no the patterns&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(442, 2, 'Hello Kitty 隨您貼神奇掛勾', '&lt;p&gt;*隨意貼掛-強力附著於安裝面&lt;br /&gt;\r\n*清水沖洗後,可重覆使用&lt;br /&gt;\r\n*美觀實用,不破壞表面&lt;br /&gt;\r\n*可重覆貼掛勾&lt;br /&gt;\r\n*台灣製造&lt;/p&gt;\r\n\r\n&lt;p&gt;注意事項:&lt;/p&gt;\r\n\r\n&lt;p&gt;*使用前請閱讀商品說明..&lt;/p&gt;\r\n\r\n&lt;p&gt;*最高承重:3kg以下&lt;/p&gt;\r\n\r\n&lt;p&gt;*玻璃(毛玻璃除外).磁磚.不鏽鋼板.烤漆板.光面大理石.PVC板.壓克力板.光滑面木板等平整光滑之牆面均適合本產品黏貼使用&lt;/p&gt;\r\n\r\n&lt;p&gt;*勿靠近火源&lt;/p&gt;\r\n\r\n&lt;p&gt;*勿用於粗糙脫落之表面及天花板&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;lt;&amp;lt;本產品已通過SGS之鄰苯二甲酸酯與歐盟EN71無毒測試&amp;gt;&amp;gt;&lt;/p&gt;\r\n', '', '', '');
