INSERT INTO `oc_product_description` VALUES(444, 2, 'Pinocchio - Hello Kitty嬰兒車/床用被夾', '&lt;p&gt;~日本原裝 ~Hello Kitty 棉被夾 嬰兒車 嬰兒床 用&lt;/p&gt;\r\n\r\n&lt;p&gt;　　♥ 使用非常簡單，可用於嬰兒車固定小被子，防止寶寶踢落被子而髒污。&lt;/p&gt;\r\n\r\n&lt;p&gt;　　♥ 用在嬰兒床邊，可防止寶寶踢被子而受寒。&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;　　♥&amp;nbsp; 長度可調整。&lt;/p&gt;\r\n\r\n&lt;p&gt;　　♥ 一組2個&lt;/p&gt;\r\n', '', '', '');
