INSERT INTO `oc_product_description` VALUES(449, 2, '米奇嬰兒護頸枕', '&lt;p&gt;&lt;span style=&quot;color: rgb(20, 24, 35); font-family: Helvetica, Arial, ''lucida grande'', tahoma, verdana, arial, sans-serif; font-size: 13.63636302947998px; line-height: 20px;&quot;&gt;好多時候寶寶坐BB車睡著時頭部會不自覺向前傾, 行車時的震動會使嬰兒頸部承受壓力, 久而久之會對寶寶的頸椎造成影響! 今次推出兩款可愛寶寶護頸枕, 可固定寶寶的頭部, 減弱行車時震動的衝擊力, 有緩衝的效果~ 而兩旁的護頸設計, 可以恰當填充肩膀空隙, 可有效地預防頸椎問題呢!! 護頸枕採用雙面物料, 四季適用, 既實用又可愛~&lt;/span&gt;&lt;br style=&quot;color: rgb(20, 24, 35); font-family: Helvetica, Arial, ''lucida grande'', tahoma, verdana, arial, sans-serif; font-size: 13.63636302947998px; line-height: 20px;&quot; /&gt;\r\n&lt;br style=&quot;color: rgb(20, 24, 35); font-family: Helvetica, Arial, ''lucida grande'', tahoma, verdana, arial, sans-serif; font-size: 13.63636302947998px; line-height: 20px;&quot; /&gt;\r\n&lt;span style=&quot;color: rgb(20, 24, 35); font-family: Helvetica, Arial, ''lucida grande'', tahoma, verdana, arial, sans-serif; font-size: 13.63636302947998px; line-height: 20px;&quot;&gt;適合漫長的車程&lt;/span&gt;&lt;span class=&quot;text_exposed_show&quot; style=&quot;display: inline; color: rgb(20, 24, 35); font-family: Helvetica, Arial, ''lucida grande'', tahoma, verdana, arial, sans-serif; font-size: 13.63636302947998px; line-height: 20px;&quot;&gt;、長途飛機，亦可用於嬰兒手推車&lt;br /&gt;\r\n適合1-4歲寶寶&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;高32cm, 寬25cm&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(450, 1, 'Peppa Pig 8 Books Set', '&lt;p align=&quot;left&quot; dir=&quot;ltr&quot; style=&quot;font-size: medium; orphans: 2; widows: 2; line-height: 22pt; font-family: Arial; color: rgb(255, 0, 0); font-weight: 700;&quot;&gt;Peppa Pig 8 Books Set&lt;/p&gt;\r\n\r\n&lt;div style=&quot;color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: x-large; line-height: normal; text-align: center; background-color: rgb(0, 102, 204);&quot;&gt;\r\n&lt;p style=&quot;text-align: left;&quot;&gt;&lt;font face=&quot;Times New Roman&quot; size=&quot;5&quot;&gt;&lt;b&gt;&lt;span class=&quot;style14&quot; style=&quot;color: rgb(0, 102, 204);&quot;&gt;&lt;font color=&quot;#00FFFF&quot;&gt;Description by Single Titles&lt;/font&gt;&lt;/span&gt;&lt;/b&gt;&lt;/font&gt;&lt;/p&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;p style=&quot;color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: x-large; line-height: normal;&quot;&gt;&lt;span style=&quot;font-family: ''Trebuchet MS''; font-size: medium; orphans: 2; widows: 2;&quot;&gt;A fantastic Peppa Pig collection that''s great value for Peppa Pig fans!! Eight chunky books in a collection. The perfect gift for your little piglet.&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;left&quot; dir=&quot;ltr&quot; style=&quot;orphans: 2; widows: 2; font-weight: 700; font-size: 13pt; line-height: 22pt; font-family: Arial; color: rgb(255, 0, 255);&quot;&gt;&lt;b style=&quot;color: rgb(0, 102, 204); font-size: large; font-family: Arial, Helvetica, sans-serif; line-height: 22pt;&quot;&gt;Title In This Set Of Book&lt;/b&gt;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;left&quot; dir=&quot;ltr&quot; style=&quot;color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: x-large; orphans: 2; widows: 2; line-height: 22pt;&quot;&gt;&lt;font size=&quot;4&quot;&gt;Colours, Squidgy Mud, Shapes, Let''s Go, How Big?, What''s the Time?, Numbers, Words.&lt;/font&gt;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `oc_product_description` VALUES(450, 2, 'Peppa Pig 早教書 (一套8 本)', '&lt;p&gt;&lt;span style=&quot;line-height: 1.6em;&quot;&gt;席捲全球的peppa pig來啦！這一次，Peppa Pig 帶著好玩有趣的配圖來教你學會算術、辨別物體大小以及顏色。選用圓角設計的紙板書仔，細心的設計呵護孩子的肌膚！撕不爛的硬紙板免去您收拾碎紙屑的煩惱，很適合0-3歲的小朋友！&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;font-size: 13px;&quot;&gt;頁數：12頁x8冊&lt;br /&gt;\r\n尺寸：13 x13cm&lt;br /&gt;\r\n出版社：Penguin Books&lt;/p&gt;\r\n', '', '', '');
